mapper.select:
SELECT 

FROM yyb_member a

WHERE a.id = #{id}

SELECT
FROM yyb_member a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.type = #{type}
AND a.id_card LIKE '%'||#{idCard}||'%' '%'+#{idCard}+'%' concat('%',#{idCard},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM yyb_member a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

