mapper.select:
SELECT

FROM yyb_musician a

WHERE a.id = #{id}

SELECT

FROM yyb_musician a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		and a.company_id = #{companyId}


		AND a.company_name LIKE
			'%'||#{companyName}||'%'
			'%'+#{companyName}+'%'
			concat('%',#{companyName},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM yyb_musician a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yyb_musician SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE yyb_musician SET
	status = #{status}
WHERE id = #{id}
