WHERE a.id = #{id}

SELECT 

FROM yyb_order_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.order_id = #{yybOrder.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

