mapper.update:
DELETE FROM yyb_order_detail


		WHERE id = #{id}


		WHERE order_id = #{yybOrder.id}

UPDATE yyb_order_detail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_id = #{yybOrder.id}
