mapper.select:
SELECT 

FROM yyb_right a

WHERE a.id = #{id}

SELECT 

FROM yyb_right a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.rate BETWEEN #{beginRate} AND #{endRate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM yyb_right a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yyb_right SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
