mapper.select:
SELECT 

FROM yyb_shopcart a

WHERE  (a.order_id is null or a.order_id = "")  and  a.id = #{id}

SELECT 

FROM yyb_shopcart a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM yyb_shopcart a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE yyb_shopcart SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE yyb_shopcart SET
	order_id = #{orderId}
WHERE id = #{shopcartId}
